// src/provider/Backend.tsx
import type { ReactNode } from 'react';
import { createContext, useContext, useState } from 'react';

// BackendContext의 타입을 정의합니다.
type BackendContextType = {
  backendUrl: string;
  selectBackend: (url: string) => void;
};

const BackendContext = createContext<BackendContextType | undefined>(undefined);

export const BackendProvider = ({ children }: { children: ReactNode }) => {
  const [backendUrl, setBackendUrl] = useState('https://api.backend1.com');

  const selectBackend = (url: string) => {
    setBackendUrl(url);
  };

  return (
    <BackendContext.Provider value={{ backendUrl, selectBackend }}>
      {children}
    </BackendContext.Provider>
  );
};

export const useBackend = () => {
  const context = useContext(BackendContext);
  if (context === undefined) {
    throw new Error('useBackend must be used within a BackendProvider');
  }
  return context;
};
