import type { UseMutationResult } from '@tanstack/react-query';
import { useMutation } from '@tanstack/react-query';
import type { AxiosResponse } from 'axios';

import { BASE_URL, fetchInstance } from '../instance';

export type RegisterData = {
  username: string;
  password: string;
  email: string;
};

export type LoginData = {
  username: string;
  password: string;
};

export type AuthResponse = {
  token: string;
  // 필요한 다른 필드를 여기에 추가할 수 있습니다.
};

const registerPath = `${BASE_URL}/api/members/register`;
const loginPath = `${BASE_URL}/api/members/login`;

const register = async (data: RegisterData): Promise<AuthResponse> => {
  const response: AxiosResponse<AuthResponse> = await fetchInstance.post(registerPath, data);
  return response.data;
};

const login = async (data: LoginData): Promise<AuthResponse> => {
  const response: AxiosResponse<AuthResponse> = await fetchInstance.post(loginPath, data);
  return response.data;
};

export const useRegister = (): UseMutationResult<AuthResponse, Error, RegisterData> => {
  return useMutation<AuthResponse, Error, RegisterData>({
    mutationFn: register,
  });
};

export const useLogin = (): UseMutationResult<AuthResponse, Error, LoginData> => {
  return useMutation<AuthResponse, Error, LoginData>({
    mutationFn: login,
  });
};
