// src/api/instance/index.ts
import { QueryClient } from '@tanstack/react-query';
import type { AxiosInstance, AxiosRequestConfig } from 'axios';
import axios from 'axios';

import { getBackendUrl } from '@/provider/Auth/Backend';

const initInstance = (baseURL: string, config?: AxiosRequestConfig): AxiosInstance => {
  return axios.create({
    baseURL,
    timeout: 5000,
    ...config,
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      ...config?.headers,
    },
  });
};

export const fetchInstance = (config?: AxiosRequestConfig): AxiosInstance => {
  const baseURL = getBackendUrl();
  return initInstance(baseURL, config);
};

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      refetchOnMount: true,
      refetchOnReconnect: true,
      refetchOnWindowFocus: true,
    },
  },
});
