import { Box, Button, Image, Text } from '@chakra-ui/react';
import styled from '@emotion/styled';
import { useEffect, useState } from 'react';

import { fetchInstance } from '@/api/instance';
import { useAuth } from '@/provider/Auth';
import { breakpoints } from '@/styles/variants';

interface WishlistItem {
  id: number;
  product: {
    id: number;
    name: string;
    price: number;
    imageUrl: string;
  };
}

export const MyAccount = () => {
  const authInfo = useAuth();
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);

  useEffect(() => {
    if (authInfo) {
      fetchInstance
        .get('/api/wishes?page=0&size=10&sort=createdDate,desc', {
          headers: {
            Authorization: `Bearer ${authInfo.token}`,
          },
        })
        .then((response) => {
          setWishlist(response.data.content);
        })
        .catch((error) => {
          console.error('Failed to fetch wishlist:', error);
        });
    }
  }, [authInfo]);

  const handleRemoveFromWishlist = async (wishId: number) => {
    if (!authInfo) {
      console.error('User is not authenticated');
      return;
    }

    try {
      await fetchInstance.delete(`/api/wishes/${wishId}`, {
        headers: {
          Authorization: `Bearer ${authInfo.token}`,
        },
      });
      setWishlist((prev) => prev.filter((item) => item.id !== wishId));
    } catch (error) {
      console.error('Failed to remove from wishlist:', error);
      alert('관심 상품 삭제 실패');
    }
  };

  return (
    <Wrapper>
      <Box mb={4}>
        <Text fontSize="2xl" fontWeight="bold">
          관심 상품 목록
        </Text>
      </Box>
      <Box>
        {wishlist.map((item) => (
          <Box key={item.id} display="flex" alignItems="center" mb={4}>
            <Image src={item.product.imageUrl} alt={item.product.name} boxSize="100px" mr={4} />
            <Box>
              <Text fontSize="lg" fontWeight="bold">
                {item.product.name}
              </Text>
              <Text>{item.product.price}원</Text>
            </Box>
            <Button ml="auto" colorScheme="red" onClick={() => handleRemoveFromWishlist(item.id)}>
              삭제
            </Button>
          </Box>
        ))}
      </Box>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;
  padding: 16px;

  @media screen and (min-width: ${breakpoints.sm}) {
    padding: 32px;
  }
`;
