import { useMutation } from '@tanstack/react-query';

import { BASE_URL, fetchInstance } from '../instance';

export type RegisterData = {
  username: string;
  password: string;
  email: string;
};

export type LoginData = {
  username: string;
  password: string;
};

const registerPath = `${BASE_URL}/api/members/register`;
const loginPath = `${BASE_URL}/api/members/login`;

const register = async (data: RegisterData) => {
  const response = await fetchInstance.post(registerPath, data);
  return response.data;
};

const login = async (data: LoginData) => {
  const response = await fetchInstance.post(loginPath, data);
  return response.data;
};

export const useRegister = () => {
  return useMutation(register);
};

export const useLogin = () => {
  return useMutation(login);
};
