// src/api/hooks/useGetProductDetail.ts
import { useSuspenseQuery } from '@tanstack/react-query';

import { useBackend } from '@/provider/Auth/Backend';
import type { ProductData } from '@/types';

import { fetchInstance } from '../instance';

export type ProductDetailRequestParams = {
  productId: string;
};

type Props = ProductDetailRequestParams;

export type GoodsDetailResponseData = ProductData;

export const getProductDetail = async (backendUrl: string, params: ProductDetailRequestParams) => {
  const response = await fetchInstance(backendUrl).get<GoodsDetailResponseData>(
    `/api/products/${params.productId}`,
  );
  return response.data;
};

export const useGetProductDetail = ({ productId }: Props) => {
  const { backendUrl } = useBackend();

  return useSuspenseQuery({
    queryKey: [`productDetail-${productId}`],
    queryFn: () => getProductDetail(backendUrl, { productId }),
  });
};
