import { useSuspenseQuery } from '@tanstack/react-query';

import { useBackend } from '@/provider/Auth/Backend'; // useBackend 훅 사용
import type { ProductData } from '@/types';

import { fetchInstance } from '../instance';

export type ProductDetailRequestParams = {
  productId: string;
};

type Props = ProductDetailRequestParams;

export type GoodsDetailResponseData = ProductData;

export const getProductDetailPath = (productId: string) => `/api/products/${productId}`; // 함수 추가

export const getProductDetail = async (params: ProductDetailRequestParams, baseURL: string) => {
  const response = await fetchInstance(baseURL).get<GoodsDetailResponseData>(
    getProductDetailPath(params.productId),
  );
  return response.data;
};

export const useGetProductDetail = ({ productId }: Props) => {
  const { backendUrl } = useBackend(); // useBackend 훅 사용

  return useSuspenseQuery({
    queryKey: [getProductDetailPath(productId)],
    queryFn: () => getProductDetail({ productId }, backendUrl),
  });
};
