import { Box, Button, HStack, Image, Text, useBreakpointValue, VStack } from '@chakra-ui/react';
import styled from '@emotion/styled';
import { useEffect } from 'react';

import { fetchInstance } from '@/api/instance';
import { useAuth } from '@/provider/Auth';
import { useWishlist } from '@/provider/Auth/Wishlist';
import { breakpoints } from '@/styles/variants';
import { authSessionStorage } from '@/utils/storage';

export const MyAccount = () => {
  const authInfo = useAuth();
  const { wishlist, removeFromWishlist, fetchWishlist } = useWishlist();

  useEffect(() => {
    if (authInfo) {
      fetchWishlist();
    }
  }, [authInfo, fetchWishlist]);

  const handleRemoveFromWishlist = async (wishId: number) => {
    if (!authInfo) {
      console.error('User is not authenticated');
      return;
    }

    try {
      await fetchInstance.delete(`/api/wishes/${wishId}`, {
        headers: {
          Authorization: `Bearer ${authInfo.token}`,
        },
      });
      removeFromWishlist(wishId);
    } catch (error) {
      console.error('Failed to remove from wishlist:', error);
      alert('관심 상품 삭제 실패');
    }
  };

  const maxWidth = useBreakpointValue({ base: '100%', sm: '600px' });

  if (wishlist.length === 0) {
    return <div>관심 상품 목록이 비어 있습니다.</div>;
  }

  return (
    <Wrapper>
      <Box mb={4} textAlign="center">
        <Text fontSize="2xl" fontWeight="bold">
          관심 상품 목록
        </Text>
      </Box>
      <VStack spacing={4} align="stretch" maxWidth={maxWidth} margin="0 auto">
        {wishlist.map((item) => (
          <HStack
            key={item.id}
            p={4}
            borderWidth="1px"
            borderRadius="lg"
            overflow="hidden"
            align="center"
            spacing={4}
            width="100%"
          >
            <Image
              src={item.product.imageUrl}
              alt={item.product.name}
              boxSize="100px"
              objectFit="cover"
            />
            <Box flex="1">
              <Text fontSize="lg" fontWeight="bold">
                {item.product.name}
              </Text>
              <Text>{item.product.price}원</Text>
            </Box>
            <Button colorScheme="red" onClick={() => handleRemoveFromWishlist(item.id)}>
              삭제
            </Button>
          </HStack>
        ))}
      </VStack>
      <Box textAlign="center" mt={8}>
        <Button
          onClick={() => {
            authSessionStorage.set(undefined);
            window.location.reload();
          }}
          colorScheme="yellow"
          size="lg"
        >
          로그아웃
        </Button>
      </Box>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;
  padding: 16px;

  @media screen and (min-width: ${breakpoints.sm}) {
    padding: 32px;
  }
`;
