// eslint-disable-next-line simple-import-sort/imports
import { AddIcon, MinusIcon } from '@chakra-ui/icons';
import { IconButton, Input, useNumberInput } from '@chakra-ui/react';
import styled from '@emotion/styled';
import { useAuth } from '@/provider/Auth';
import { fetchInstance } from '@/api/instance';
import { useBackend } from '@/provider/Auth/Backend';

type Props = {
  productId: string;
};

export const OptionSection = ({ productId }: Props) => {
  const { getInputProps, getIncrementButtonProps, getDecrementButtonProps } = useNumberInput({
    step: 1,
    min: 1,
    max: 100,
    defaultValue: 1,
  });

  const increment = getIncrementButtonProps();
  const decrement = getDecrementButtonProps();
  const input = getInputProps();
  const { backendUrl } = useBackend();

  const handleAddToWishList = async () => {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const token = useAuth()?.token;
    if (!token) {
      alert('로그인이 필요합니다.');
      return;
    }

    try {
      const response = await fetchInstance(backendUrl).post(
        '/api/wishes',
        { productId: parseInt(productId) },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },
      );

      if (response.status === 201) {
        alert('관심 등록 완료');
      } else {
        alert('관심 등록 실패');
      }
    } catch (error) {
      console.error('Failed to add to wishlist:', error);
      alert('관심 등록 실패');
    }
  };

  return (
    <Wrapper>
      <Title>상품 옵션</Title>
      <InputWrapper>
        <IconButton {...decrement} aria-label="수량 1개 감소" icon={<MinusIcon />} />
        <Input {...input} />
        <IconButton {...increment} aria-label="수량 1개 추가" icon={<AddIcon />} />
      </InputWrapper>
      <Button onClick={handleAddToWishList}>관심상품 추가</Button>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 100%;
  padding: 12px 14px 16px;
  border: 1px solid #ededed;
  border-radius: 2px;
`;

const Title = styled.p`
  font-weight: 700;
  line-height: 22px;
  color: #111;
  word-wrap: break-word;
  word-break: break-all;
`;

const InputWrapper = styled.div`
  display: flex;
  justify-content: center;
  padding-top: 8px;
  gap: 8px;
`;

const Button = styled.button`
  margin-top: 16px;
  padding: 8px 16px;
  background-color: #ffcc00;
  border: none;
  border-radius: 4px;
  color: #000;
  cursor: pointer;

  &:hover {
    background-color: #ffdb4d;
  }
`;
