import styled from '@emotion/styled';
import axios from 'axios';
import { useEffect, useState } from 'react';

import { Button } from '@/components/common/Button';
import { Spacing } from '@/components/common/layouts/Spacing';
import { useAuth } from '@/provider/Auth';
import type { WishlistItem, WishlistResponse } from '@/types';

export const MyAccountPage = () => {
  const authInfo = useAuth();
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const response = await axios.get<WishlistResponse>('/api/wishes', {
          headers: {
            Authorization: `Bearer ${authInfo?.token}`,
          },
        });
        setWishlist(response.data.content);
      } catch (error) {
        console.error('Failed to fetch wishlist:', error);
      }
    };

    fetchWishlist();
  }, [authInfo]);

  const handleRemoveFromWishList = async (wishId: number) => {
    try {
      await axios.delete(`/api/wishes/${wishId}`, {
        headers: {
          Authorization: `Bearer ${authInfo?.token}`,
        },
      });
      setWishlist(wishlist.filter((item) => item.id !== wishId));
    } catch (error) {
      console.error('Failed to remove from wishlist:', error);
      alert('관심 목록 삭제 실패');
    }
  };

  return (
    <Wrapper>
      <h2>관심 목록</h2>
      <Spacing height={20} />
      {wishlist.map((item) => (
        <StyledWishlistItem key={item.id}>
          <img src={item.product.imageUrl} alt={item.product.name} />
          <div>
            <h3>{item.product.name}</h3>
            <p>{item.product.price}원</p>
          </div>
          <Button onClick={() => handleRemoveFromWishList(item.id)}>삭제</Button>
        </StyledWishlistItem>
      ))}
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 100%;
  padding: 80px 0 120px;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 36px;
`;

const StyledWishlistItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;

  img {
    width: 100px;
    height: 100px;
    margin-right: 20px;
  }

  div {
    flex-grow: 1;

    h3 {
      margin: 0;
      font-size: 20px;
    }

    p {
      margin: 0;
      font-size: 16px;
      color: #666;
    }
  }

  button {
    margin-left: 20px;
  }
`;
