import { Box, Button, HStack, Image, Text, useBreakpointValue, VStack } from '@chakra-ui/react';
import styled from '@emotion/styled';
import { useEffect, useState } from 'react';

import { fetchInstance } from '@/api/instance';
import { useAuth } from '@/provider/Auth';
import { breakpoints } from '@/styles/variants';

interface WishlistItem {
  id: number;
  product: {
    id: number;
    name: string;
    price: number;
    imageUrl: string;
  };
}

export const MyAccount = () => {
  const authInfo = useAuth();
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);

  useEffect(() => {
    if (authInfo) {
      fetchInstance
        .get('/api/wishes?page=0&size=10&sort=createdDate,desc', {
          headers: {
            Authorization: `Bearer ${authInfo.token}`,
          },
        })
        .then((response) => {
          console.log('Wishlist API response:', response.data);
          setWishlist(response.data.content);
        })
        .catch((error) => {
          console.error('Failed to fetch wishlist:', error);
        });
    }
  }, [authInfo]);

  const handleRemoveFromWishlist = async (wishId: number) => {
    if (!authInfo) {
      console.error('User is not authenticated');
      return;
    }

    try {
      await fetchInstance.delete(`/api/wishes/${wishId}`, {
        headers: {
          Authorization: `Bearer ${authInfo.token}`,
        },
      });
      setWishlist((prev) => prev.filter((item) => item.id !== wishId));
    } catch (error) {
      console.error('Failed to remove from wishlist:', error);
      alert('관심 상품 삭제 실패');
    }
  };

  const handleLogout = () => {
    // 로그아웃 처리 로직
    authInfo.logout();
  };

  const maxWidth = useBreakpointValue({ base: '100%', sm: '600px' });

  return (
    <Wrapper>
      <Box mb={4} textAlign="center">
        <Text fontSize="2xl" fontWeight="bold">
          관심 상품 목록
        </Text>
        <Button colorScheme="yellow" onClick={handleLogout} mt={4}>
          로그아웃
        </Button>
      </Box>
      <VStack spacing={4} align="stretch" maxWidth={maxWidth} margin="0 auto">
        {wishlist.map((item) => (
          <HStack
            key={item.id}
            p={4}
            borderWidth="1px"
            borderRadius="lg"
            overflow="hidden"
            align="center"
            spacing={4}
            width="100%"
          >
            <Image
              src={item.product.imageUrl}
              alt={item.product.name}
              boxSize="100px"
              objectFit="cover"
            />
            <Box flex="1">
              <Text fontSize="lg" fontWeight="bold">
                {item.product.name}
              </Text>
              <Text>{item.product.price}원</Text>
            </Box>
            <Button colorScheme="red" onClick={() => handleRemoveFromWishlist(item.id)}>
              삭제
            </Button>
          </HStack>
        ))}
      </VStack>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;
  padding: 16px;

  @media screen and (min-width: ${breakpoints.sm}) {
    padding: 32px;
  }
`;
