import { useQuery } from '@tanstack/react-query';

import { useBackend } from '@/provider/Auth/Backend';
import type { CategoryData } from '@/types';

import { fetchInstance } from '../instance';

export type CategoryResponseData = CategoryData[];

export const getCategoriesPath = () => `/api/categories/`; // 함수 추가

export const getCategories = async (baseURL: string) => {
  const response = await fetchInstance(baseURL).get<CategoryResponseData>(getCategoriesPath());
  return response.data;
};

export const useGetCategories = () => {
  const { backendUrl } = useBackend();

  return useQuery({
    queryKey: ['categories'],
    queryFn: () => getCategories(backendUrl),
  });
};
