import type { ReactNode } from 'react';
import React, { createContext, useCallback, useContext, useEffect, useState } from 'react';

import { fetchInstance } from '@/api/instance';
import { useAuth } from '@/provider/Auth';

interface WishlistItem {
  id: number;
  product: {
    id: number;
    name: string;
    price: number;
    imageUrl: string;
  };
}

interface WishlistContextProps {
  wishlist: WishlistItem[];
  addToWishlist: (item: WishlistItem) => void;
  removeFromWishlist: (wishId: number) => void;
  fetchWishlist: () => Promise<void>;
}

const WishlistContext = createContext<WishlistContextProps | undefined>(undefined);

interface WishlistProviderProps {
  children: ReactNode;
}

export const WishlistProvider: React.FC<WishlistProviderProps> = ({ children }) => {
  const authInfo = useAuth();
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);

  useEffect(() => {
    if (authInfo) {
      fetchWishlist();
    }
  }, [authInfo, fetchWishlist]); // fetchWishlist 추가

  // eslint-disable-next-line react-hooks/exhaustive-deps
  const fetchWishlist = useCallback(async () => {
    if (authInfo) {
      try {
        const response = await fetchInstance.get(
          '/api/wishes?page=0&size=10&sort=createdDate,desc',
          {
            headers: {
              Authorization: `Bearer ${authInfo.token}`,
            },
          },
        );
        setWishlist(response.data.content);
      } catch (error) {
        console.error('Failed to fetch wishlist:', error);
      }
    }
  });

  const addToWishlist = (item: WishlistItem) => {
    setWishlist((prev) => [...prev, item]);
  };

  const removeFromWishlist = (wishId: number) => {
    setWishlist((prev) => prev.filter((item) => item.id !== wishId));
  };

  return (
    <WishlistContext.Provider
      value={{ wishlist, addToWishlist, removeFromWishlist, fetchWishlist }}
    >
      {children}
    </WishlistContext.Provider>
  );
};

export const useWishlist = () => {
  const context = useContext(WishlistContext);
  if (!context) {
    throw new Error('useWishlist must be used within a WishlistProvider');
  }
  return context;
};
