// src/components/features/Category/CategoryProductsSection/index.tsx
import { Grid } from '@chakra-ui/react';
import styled from '@emotion/styled';
import { useParams } from 'react-router-dom';
import { DefaultGoodsItems } from '@/components/common/Goods/DefaultGoodsItems';
import { useGetProducts } from '@/api/hooks/useGetProducts';
import { getDynamicPath } from '@/routes/path';

type Props = {
  categoryId: string;
};

export const CategoryProductsSection = ({ categoryId }: Props) => {
  const { data, fetchNextPage, hasNextPage, isError } = useGetProducts({ categoryId });
  const flattenGoodsList = data?.pages.flatMap((page) => page.products) ?? [];

  if (isError) return <TextView>에러가 발생했습니다.</TextView>;
  if (!data || flattenGoodsList.length === 0) return <TextView>상품이 없어요.</TextView>;

  return (
    <Wrapper>
      <Grid gap={16}>
        {flattenGoodsList.map(({ id, imageUrl, name, price }) => (
          <Link key={id} to={getDynamicPath.productsDetail(id)}>
            <DefaultGoodsItems key={id} id={id} imageUrl={imageUrl} name={name} price={price} />
          </Link>
        ))}
      </Grid>
      {hasNextPage && <button onClick={() => fetchNextPage()}>더 보기</button>}
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 100%;
  padding: 16px;
`;

const TextView = styled.p`
  text-align: center;
  padding: 16px;
  color: #666;
`;
