// src/provider/Backend.tsx
import React, { createContext, useContext, useState } from 'react';

const BackendContext = createContext<unknown>(null);

export const BackendProvider = ({ children }: { children: React.ReactNode }) => {
  const [backendUrl, setBackendUrl] = useState('https://api.backend1.com');

  const selectBackend = (url: string) => {
    setBackendUrl(url);
  };

  return (
    <BackendContext.Provider value={{ backendUrl, selectBackend }}>
      {children}
    </BackendContext.Provider>
  );
};

export const useBackend = () => useContext(BackendContext);
