// src/api/hooks/useGetCategories.ts
import { useQuery } from '@tanstack/react-query';

import { useBackend } from '@/provider/Auth/Backend';
import type { CategoryData } from '@/types';

import { fetchInstance } from '../instance';

export type CategoryResponseData = CategoryData[];

export const getCategories = async (backendUrl: string) => {
  const response = await fetchInstance(backendUrl).get<CategoryResponseData>('/api/categories');
  return response.data;
};

export const useGetCategories = () => {
  const { backendUrl } = useBackend();

  return useQuery({
    queryKey: ['categories'],
    queryFn: () => getCategories(backendUrl),
  });
};
