import styled from '@emotion/styled';
import type { AxiosError } from 'axios';

import { useGetProductDetail } from '@/api/hooks/useGetProductDetail';
import { fetchInstance } from '@/api/instance';
import { Button } from '@/components/common/Button';
import { useWishlist } from '@/provider/Auth/Wishlist';
import { breakpoints } from '@/styles/variants';
import { authSessionStorage } from '@/utils/storage';

import { GoodsDetailHeader } from './Header';

const getToken = () => {
  return authSessionStorage.get();
};

export const GoodsDetail = ({ productId }: { productId: string }) => {
  const { data: productDetail } = useGetProductDetail({ productId });
  const { addToWishlist } = useWishlist();

  const handleAddToWishList = async () => {
    const token = getToken();
    if (!token) {
      alert('로그인이 필요합니다.');
      return;
    }

    console.log('Attempting to add to wishlist:', {
      productId: parseInt(productId),
      token,
    });

    try {
      const response = await fetchInstance.post(
        '/api/wishes',
        { productId: parseInt(productId) },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },
      );

      console.log('API response:', response);

      if (response.status === 201) {
        alert('관심 등록 완료');
        addToWishlist(response.data);
      } else {
        alert('관심 등록 실패');
      }
    } catch (error) {
      const axiosError = error as AxiosError;
      if (axiosError.response) {
        console.error('API error response:', axiosError.response);
        if (axiosError.response.status === 401) {
          alert('인증이 필요합니다.');
        } else if (axiosError.response.status === 400) {
          alert('잘못된 요청입니다.');
        } else if (axiosError.response.status === 404) {
          alert('상품을 찾을 수 없습니다.');
        } else {
          alert('관심 등록 실패');
        }
      } else {
        console.error('Failed to add to wishlist:', error);
        alert('관심 등록 실패');
      }
    }
  };

  return (
    <Wrapper>
      <GoodsDetailHeader productId={productId} />
      <Button onClick={handleAddToWishList}>관심 등록</Button>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;
  padding: 16px 16px 60px;

  @media screen and (min-width: ${breakpoints.sm}) {
    padding: 32px 32px 80px;
  }
`;
